(85, 'HR Head', 2, '<li class=\"career__qual-item\">Graduate of Human Resource Management, Psychology or any related courses</li><li class=\"career__qual-item\">At least 3 years HR Generalist functions in a retail environment/corporate and management responsibility</li><li class=\"career__qual-item\">At least 3 years solid experience in organization and development, talent retention and including functional executive/leadership support</li><li class=\"career__qual-item\">Strong communication, leadership and influencing skills</li><li class=\"career__qual-item\">Experience in retail industry is an advantage</li><li class=\"career__qual-item\">Willing to travel anywhere in the Philippines</li>', 0, NULL, 1, '2019-09-03 15:31:57', '2019-09-19 09:19:09'),
(68, 'Messenger', 2, '•	Graduate of any 2-year vocational course or at least 2nd level in College of any course<div>•	At least 6 months of work experience in documentation or any related field</div><div>•	With Driver\'s License (1,2)</div><div>•	Knows how to drive a motorcycle/four wheeled vehicle</div><div>•	Preferably with own Motorcycle&nbsp;</div>', 1, NULL, 13, '2019-01-03 08:45:02', '2020-02-27 13:40:17'),
(72, 'Master Data Clerk', 2, '<li class=\'career__qual-item\'>Graduate of Accounting Technology</li><li class=\'career__qual-item\'>Detail-oriented personality with analytical skills</li><li class=\'career__qual-item\'>Highly organized</li><li class=\'career__qual-item\'>Proven ability to work independently and as a team member</li><li class=\'career__qual-item\'>Good in working with numbers and data</li>', 0, NULL, 11, '2019-01-28 14:24:33', '2019-04-29 14:55:29'),
(69, 'Construction Auditor', 2, '<li class=\'career__qual-item\'>Graduate of Accountancy or any Engineering courses</li><li class=\'career__qual-item\'>At least 5 years working experience in construction auditing</li><li class=\'career__qual-item\'>At least 1 year supervisory experience</li><li class=\'career__qual-item\'>Strong written and oral communication skills</li><li class=\'career__qual-item\'>Detail-oriented</li><li class=\'career__qual-item\'>Preferably CPA or License Engineer</li><li class=\'career__qual-item\'>Willing to be assigned to any construction sites anywhere in the Philippines</li>', 0, NULL, 2, '2019-01-07 14:04:58', '2019-03-12 09:13:12'),
